jQuery.scroller=function(document){var scroller=$({});var html=document.documentElement;var body=document.body;scroller.marginBottom=0;scroller.initialize=function(){scroller.updateWindowInformations();$(document).on("scroll",function(event){scroller.onscroll(event)});$(window).on("resize",function(event){scroller.updateWindowInformations(event)});delete scroller.initialize};scroller.onscroll=function(event){scroller.top=body.scrollTop||html.scrollTop;scroller.trigger("scroll",event);if(this.isOnBottom()){scroller.trigger("scrolltoend",event)}};scroller.isOnBottom=function(){return this.top+this.windowHeight+this.marginBottom>=this.pageHeight};scroller.updateWindowInformations=function(){this.pageHeight=body.scrollHeight;this.windowHeight=html.clientHeight};scroller.initialize();return scroller}(document);